import React, { useEffect, useState } from "react";
import axios from "axios";
import Modal from "react-modal";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

type Email = {
  id: number;
  name: string;
  amount: number;
  comments: string;
};

const App: React.FC = () => {
  const [emails, setEmails] = useState<Email[]>([]);
  const [selectedEmail, setSelectedEmail] = useState<Email | null>(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  useEffect(() => {
    fetchEmails();
  }, []);

  const fetchEmails = () => {
    axios.get("/api/emails").then((res) => {
      setEmails(res.data);
    });
  };

  const handleDeleteEmail = (id: number) => {
    axios.delete(`/api/emails/${id}`).then(() => {
      toast.success("Email deleted successfully");
      fetchEmails();
    });
  };
  const handleUpdateEmail = () => {
    if (selectedEmail) {
      axios.put(`/api/emails/${selectedEmail.id}`, selectedEmail).then(() => {
        toast.success("Email updated successfully");
        fetchEmails();
        setModalIsOpen(false);
      });
    }
  };

  const openModal = (email: Email) => {
    setSelectedEmail(email);
    setModalIsOpen(true);
  };
  const closeModal = () => {
    setModalIsOpen(false);
    setSelectedEmail(null);
  };

  return (
    <div>
      <h1>Email Data</h1>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Amount</th>
            <th>Comments</th>
          </tr>
        </thead>
        <tbody>
          {emails.map((email) => (
            <tr key={email.id}>
              <td>{email.name}</td>
              <td>{email.amount}</td>
              <td>{email.comments}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div>
        <h2>Add New Email</h2>
        <input
          type="text"
          placeholder="Name"
          value={newEmail.name}
          onChange={(e) => setNewEmail({ ...newEmail, name: e.target.value })}
        />
        <input
          type="number"
          placeholder="Amount"
          value={newEmail.amount}
          onChange={(e) =>
            setNewEmail({ ...newEmail, amount: parseFloat(e.target.value) })
          }
        />
        <input
          type="text"
          placeholder="Comments"
          value={newEmail.comments}
          onChange={(e) =>
            setNewEmail({ ...newEmail, comments: e.target.value })
          }
        />
        <button onClick={handleAddEmail}>Add Email</button>
      </div>
    </div>
  );
};

export default App;
